import React, { useState } from "react";
import './index.css';

const GenPass =() => {
    const [password , setpassword ]=useState("");
    const [upperCase , setupperCase]=useState(false);
    const [Numbers , setnumber]=useState(false);
    const [Symbols , setsymbols]=useState(false);
    const [length , setlength]=useState(10);

    const generatePassword = () => {
        // Define character sets
        const lowercaseChars = 'abcdefghijklmnopqrstuvwxyz';
        const uppercaseChars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';
        const numberChars = '0123456789';
        const specialChars = '!@#$%^&*()_+[]{}|;:,.<>?';
    
        // Combine character sets based on user preference
        let validChars = lowercaseChars;
        if (upperCase) validChars += uppercaseChars;
        if (Numbers) validChars += numberChars;
        if (Symbols) validChars += specialChars;
    
        var p = '';
    
        // Generate password
        for (let i = 0; i < length; i++) {
          const randomIndex = Math.floor(Math.random() * validChars.length);
          p += validChars.charAt(randomIndex);
          
        }
        setpassword(p);
    }
    return (
            <div className="h-20 text-black-500 text-2xl py-2 ">
                <div >
                    <input type="text" placeholder="Password" class="text-mediumbold px-4 text-blue-600 bg-blue-900 w-full h-12 rounded-md "  value={password} disabled></input>
                </div>
                
                <div class="border-b border-transparent h-60 rounded bg-blue-900 my-2">
                    length : <span class="text-right ">{length} </span>
                    <input type="range" class="mt-1 w-full focus:outline-none focus:shadow-outline-blue focus:border-blue-300" min={8} max={25}  value={8} onChange={(e) => setlength(e.target.value)}></input><br/>
                    Allow Number :<input type="checkbox" onChange={() => setnumber(!Numbers)}></input><br/>
                    Allow upperCase :<input type="checkbox" onChange={() => setupperCase(!upperCase)}></input><br/>
                    Allow symbols :<input type="checkbox" onChange={() => setsymbols(!Symbols)}></input>
                </div>
                <div class="flex justify-center w-full">
                    <button onClick={generatePassword} class=" my-2 bg-black-900 text-gray-300 border-spacing-4 border w-32 border-black rounded-md " >Generate</button>
                </div>
            </div>
    )
}
export default GenPass ;
